package arraylist;

import java.util.ArrayList;

public class Size {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ArrayList<String> obj = new ArrayList<String>();
		obj.add("Blue");
		obj.add("Yellow");
		obj.add("Black");
		
		int a =obj.size();
		System.out.println(a);//or//
		System.out.println(obj.size());
	}

}
